//1부터 10 출력
public class 반복문_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for(int i=1 ; i<=10;i++)
		{
			System.out.print(i+" ");
		}
		System.out.println();
		
		for(int i=10 ;i>=1;i--)
		{
			System.out.print(i+" ");
		}
		System.out.println();
		
		for(char a='A';a<='Z';a++)
		{
			System.out.print(a+" ");
		}
		System.out.println();
		
		for(char a='Z' ;a>='A';a--)
		{
			System.out.print(a+" ");
		}
	
	
	
	}

}
