public class 반복문_3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//1~10까지 출력
		
		int i=1;
		while(i<=10)
		{
			System.out.print(i+" ");
			i++;
		}
		System.out.println();
		
		for(int j=1 ; j<=10 ; j++)
		{
			System.out.print(j+" ");
		}
		
	}

}
